import React, { useState } from 'react';
import CodingChallenge from './CodingChallenge';
import ChallengeRoom from './ChallengeRoom';
import OldHome from './OldHome';

function Arena() {
  const [mode, setMode] = useState('home');
  const [roomId, setRoomId] = useState('');

  const handleStartPractice = () => setMode('practice');
  const handleStartChallenge = () => setMode('challenge');
  const handleExitChallenge = () => {
    setMode('home');
    setRoomId('');
  };

  // You can add more handlers as needed for challenge creation/joining

  return (
    <>
      {mode === 'home' && (
        <OldHome
          onStartPractice={handleStartPractice}
          onStartChallenge={handleStartChallenge}
        />
      )}
      {mode === 'practice' && <CodingChallenge />}
      {mode === 'challenge' && (
        <ChallengeRoom
          roomId={roomId}
          onExit={handleExitChallenge}
        />
      )}
    </>
  );
}

export default Arena;
