import React, { useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  Paper,
  Container,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  IconButton,
  Grid,
  Snackbar,
  Alert
} from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import { socket } from '../socket';

const Home = ({ onStartPractice, onStartChallenge }) => {
  const [roomId, setRoomId] = useState('');
  const [joinDialogOpen, setJoinDialogOpen] = useState(false);
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [error, setError] = useState(null);

  const handleCreateChallenge = () => {
    console.log('Creating new challenge...');
    socket.emit('createChallenge', (response) => {
      console.log('Create challenge response:', response);
      if (response.error) {
        setError(response.error);
        return;
      }
      if (response.roomId) {
        setRoomId(response.roomId);
        setCreateDialogOpen(true);
      }
    });
  };

  const handleStartChallenge = () => {
    setCreateDialogOpen(false);
    onStartChallenge(roomId);
  };

  const handleJoinChallenge = () => {
    if (roomId.trim()) {
      onStartChallenge(roomId);
      setJoinDialogOpen(false);
    }
  };

  const handleCopyRoomId = () => {
    navigator.clipboard.writeText(roomId);
  };

  const handleCloseError = () => {
    setError(null);
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 8 }}>
        <Paper elevation={3} sx={{ p: 4 }}>
          <Typography variant="h4" align="center" gutterBottom>
            Coding Challenge Platform
          </Typography>
          <Typography variant="body1" align="center" sx={{ mb: 4 }}>
            Practice coding or challenge your friends!
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Button
                variant="contained"
                color="primary"
                size="large"
                fullWidth
                onClick={onStartPractice}
              >
                Practice Mode
              </Button>
            </Grid>
            <Grid item xs={12}>
              <Typography variant="h6" align="center" sx={{ my: 2 }}>
                - OR -
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <Button
                variant="contained"
                color="secondary"
                size="large"
                fullWidth
                onClick={handleCreateChallenge}
              >
                Create New Challenge
              </Button>
            </Grid>
            <Grid item xs={12}>
              <Button
                variant="outlined"
                color="secondary"
                size="large"
                fullWidth
                onClick={() => setJoinDialogOpen(true)}
              >
                Join Challenge
              </Button>
            </Grid>
          </Grid>
        </Paper>
      </Box>

      {/* Join Challenge Dialog */}
      <Dialog open={joinDialogOpen} onClose={() => setJoinDialogOpen(false)}>
        <DialogTitle>Join Challenge</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Enter the challenge code provided by your friend:
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            label="Challenge Code"
            fullWidth
            variant="outlined"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setJoinDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleJoinChallenge} color="primary" variant="contained">
            Join
          </Button>
        </DialogActions>
      </Dialog>

      {/* Create Challenge Dialog */}
      <Dialog open={createDialogOpen} onClose={() => setCreateDialogOpen(false)}>
        <DialogTitle>Challenge Created!</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Share this code with your opponent to start the challenge:
          </DialogContentText>
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 1,
            mt: 2,
            p: 2,
            bgcolor: 'background.paper',
            borderRadius: 1,
            border: '1px solid',
            borderColor: 'divider'
          }}>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              {roomId}
            </Typography>
            <IconButton onClick={handleCopyRoomId} color="primary" size="small">
              <ContentCopyIcon />
            </IconButton>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCreateDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleStartChallenge} color="primary" variant="contained">
            Start Challenge
          </Button>
        </DialogActions>
      </Dialog>

      {/* Error Snackbar */}
      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={handleCloseError}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseError} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default Home;
