import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Paper,
  Typography,
  Button,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import Editor from '@monaco-editor/react';

const SAMPLE_PROBLEM = {
  title: "Two Sum",
  description: `Given an array of integers nums and an integer target, return indices of the two numbers in nums such that they add up to target.
You may assume that each input would have exactly one solution, and you may not use the same element twice.
You can return the answer in any order.

Example:
Input: nums = [2,7,11,15], target = 9
Output: [0,1]
Explanation: Because nums[0] + nums[1] == 9, we return [0, 1].`,
  timeLimit: 30 // minutes
};

function CodingChallenge({ socket, username, sessionId, opponent }) {
  const [problem, setProblem] = useState(SAMPLE_PROBLEM);
  const [code, setCode] = useState('// Write your solution here\n');
  const [language, setLanguage] = useState('javascript');
  const [output, setOutput] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [timeLeft, setTimeLeft] = useState(null);

  useEffect(() => {
    if (sessionId) {
      setTimeLeft(problem.timeLimit * 60); // Convert to seconds
    }
  }, [sessionId, problem.timeLimit]);

  useEffect(() => {
    let timer;
    if (timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [timeLeft]);

  useEffect(() => {
    socket.on('waiting_for_challenger', () => {
      setIsSearching(true);
    });

    socket.on('code_evaluation', (data) => {
      setOutput(data.evaluation);
    });

    socket.on('opponent_disconnected', () => {
      setOutput('Opponent disconnected!');
    });

    return () => {
      socket.off('waiting_for_challenger');
      socket.off('code_evaluation');
      socket.off('opponent_disconnected');
    };
  }, [socket]);

  const handleSearchChallenger = () => {
    socket.emit('search_challenger', {
      problem: SAMPLE_PROBLEM,
      timeLimit: SAMPLE_PROBLEM.timeLimit
    });
  };

  const handleSubmitCode = () => {
    socket.emit('submit_code', {
      sessionId,
      code,
      language
    });
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  return (
    <Box sx={{ height: '100%' }}>
      <Grid container spacing={2} sx={{ height: '100%' }}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2, height: '100%' }}>
            <Typography variant="h5" gutterBottom>
              {problem.title}
            </Typography>
            <Typography variant="body1" sx={{ whiteSpace: 'pre-line', mb: 2 }}>
              {problem.description}
            </Typography>
            {timeLeft && (
              <Typography variant="h6" color="primary">
                Time Left: {formatTime(timeLeft)}
              </Typography>
            )}
            {opponent && (
              <Typography variant="subtitle1" sx={{ mt: 2 }}>
                Competing against: {opponent}
              </Typography>
            )}
            {!sessionId && (
              <Button
                variant="contained"
                onClick={handleSearchChallenger}
                disabled={isSearching}
                sx={{ mt: 2 }}
                fullWidth
              >
                {isSearching ? 'Searching for opponent...' : 'Find Challenger'}
              </Button>
            )}
          </Paper>
        </Grid>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 2, height: '100%' }}>
            <Box sx={{ mb: 2 }}>
              <FormControl sx={{ minWidth: 120 }}>
                <InputLabel>Language</InputLabel>
                <Select
                  value={language}
                  label="Language"
                  onChange={(e) => setLanguage(e.target.value)}
                >
                  <MenuItem value="javascript">JavaScript</MenuItem>
                  <MenuItem value="python">Python</MenuItem>
                  <MenuItem value="java">Java</MenuItem>
                </Select>
              </FormControl>
            </Box>
            <Editor
              height="400px"
              language={language}
              value={code}
              onChange={setCode}
              theme="vs-dark"
              options={{
                minimap: { enabled: false },
                fontSize: 14,
              }}
            />
            <Button
              variant="contained"
              onClick={handleSubmitCode}
              disabled={!sessionId}
              sx={{ mt: 2 }}
            >
              Submit Solution
            </Button>
            {output && (
              <Paper sx={{ mt: 2, p: 2, bgcolor: 'background.paper' }}>
                <Typography variant="h6" gutterBottom>
                  Output:
                </Typography>
                <Typography
                  variant="body2"
                  component="pre"
                  sx={{
                    whiteSpace: 'pre-wrap',
                    wordBreak: 'break-word',
                    fontFamily: 'monospace',
                  }}
                >
                  {output}
                </Typography>
              </Paper>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
}

export default CodingChallenge;
