import React, { useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  Paper,
  Container,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  IconButton,
} from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import { socket } from '../socket';
import ChallengeRoom from './ChallengeRoom';

const Home = () => {
  const [mode, setMode] = useState('select'); // 'select', 'create', 'join', 'challenge'
  const [roomId, setRoomId] = useState('');
  const [joinDialogOpen, setJoinDialogOpen] = useState(false);
  const [createDialogOpen, setCreateDialogOpen] = useState(false);

  const handleCreateChallenge = () => {
    socket.emit('createChallenge', (response) => {
      if (response.roomId) {
        setRoomId(response.roomId);
        setCreateDialogOpen(true);
      }
    });
  };

  const handleStartChallenge = () => {
    setCreateDialogOpen(false);
    setMode('challenge');
  };

  const handleJoinChallenge = () => {
    if (roomId.trim()) {
      setMode('challenge');
      setJoinDialogOpen(false);
    }
  };

  const handleCopyRoomId = () => {
    navigator.clipboard.writeText(roomId);
  };

  const handleExit = () => {
    setMode('select');
    setRoomId('');
  };

  if (mode === 'challenge') {
    return <ChallengeRoom roomId={roomId} onExit={handleExit} />;
  }

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 8 }}>
        <Paper elevation={3} sx={{ p: 4 }}>
          <Typography variant="h4" align="center" gutterBottom>
            Coding Challenge Platform
          </Typography>
          <Typography variant="body1" align="center" sx={{ mb: 4 }}>
            Challenge your friends to a coding duel!
          </Typography>

          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <Button
              variant="contained"
              color="primary"
              size="large"
              fullWidth
              onClick={handleCreateChallenge}
            >
              Create New Challenge
            </Button>
            <Button
              variant="outlined"
              color="primary"
              size="large"
              fullWidth
              onClick={() => setJoinDialogOpen(true)}
            >
              Join Challenge
            </Button>
          </Box>
        </Paper>
      </Box>

      {/* Join Challenge Dialog */}
      <Dialog open={joinDialogOpen} onClose={() => setJoinDialogOpen(false)}>
        <DialogTitle>Join Challenge</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Challenge Code"
            fullWidth
            variant="outlined"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setJoinDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleJoinChallenge} color="primary">
            Join
          </Button>
        </DialogActions>
      </Dialog>

      {/* Create Challenge Dialog */}
      <Dialog open={createDialogOpen} onClose={() => setCreateDialogOpen(false)}>
        <DialogTitle>Challenge Created!</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Share this code with your opponent to start the challenge:
          </DialogContentText>
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 1,
            mt: 2,
            p: 2,
            bgcolor: 'background.paper',
            borderRadius: 1,
            border: '1px solid',
            borderColor: 'divider'
          }}>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              {roomId}
            </Typography>
            <IconButton onClick={handleCopyRoomId} color="primary" size="small">
              <ContentCopyIcon />
            </IconButton>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCreateDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleStartChallenge} color="primary" variant="contained">
            Start Challenge
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Home;
