import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Home from './components/Home';
import CodingChallenge from './components/CodingChallenge';
import ChallengeRoom from './components/ChallengeRoom';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#90caf9',
    },
    secondary: {
      main: '#f48fb1',
    },
  },
});

function App() {
  const [mode, setMode] = useState('home'); // 'home', 'practice', or 'challenge'
  const [roomId, setRoomId] = useState(null);

  const handleStartPractice = () => {
    setMode('practice');
  };

  const handleStartChallenge = (challengeRoomId) => {
    setRoomId(challengeRoomId);
    setMode('challenge');
  };

  const handleExitChallenge = () => {
    setMode('home');
    setRoomId(null);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      {mode === 'home' && (
        <Home 
          onStartPractice={handleStartPractice}
          onStartChallenge={handleStartChallenge}
        />
      )}
      {mode === 'practice' && (
        <CodingChallenge />
      )}
      {mode === 'challenge' && (
        <ChallengeRoom 
          roomId={roomId}
          onExit={handleExitChallenge}
        />
      )}
    </ThemeProvider>
  );
}

export default App;
