import React, { useState, useEffect } from 'react';
import EditorWrapper from './EditorWrapper';
import { socket } from '../socket';

const ChallengeRoom = ({ roomId, onExit }) => {
  const [challenge, setChallenge] = useState(null);
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('python');
  const [status, setStatus] = useState('waiting');
  const [result, setResult] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });
  const [startTime, setStartTime] = useState(null);

  useEffect(() => {
    if (!roomId) return;

    socket.emit('joinChallenge', roomId, (response) => {
      if (response.error) {
        setNotification({
          open: true,
          message: response.error,
          severity: 'error'
        });
        onExit();
        return;
      }
      
      setChallenge(response.challenge);
      setCode(response.challenge.boilerplateCode[language]);
      if (response.started && response.startTime) {
        setStatus('started');
        setStartTime(response.startTime);
        const elapsed = Math.floor((Date.now() - response.startTime) / 1000);
        setTimeLeft(Math.max(0, response.challenge.timeLimit - elapsed));
      }
    });

    socket.on('gameStart', ({ challenge, startTime }) => {
      setChallenge(challenge);
      setStatus('started');
      setStartTime(startTime);
      setTimeLeft(challenge.timeLimit);
      setNotification({
        open: true,
        message: 'Game started! Good luck!',
        severity: 'success'
      });
    });

    socket.on('submissionResult', (validation) => {
      setResult(validation);
      if (validation.error) {
        setNotification({
          open: true,
          message: `Error: ${validation.error}`,
          severity: 'error'
        });
      } else if (validation.passed) {
        setNotification({
          open: true,
          message: 'All test cases passed!',
          severity: 'success'
        });
      } else {
        setNotification({
          open: true,
          message: 'Some test cases failed. Check the results below.',
          severity: 'warning'
        });
      }
    });

    socket.on('gameOver', ({ winnerId, solutions }) => {
      setStatus('finished');
      setResult({
        isWinner: socket.id === winnerId,
        solutions
      });
      setNotification({
        open: true,
        message: socket.id === winnerId ? 'Congratulations! You won! ðŸŽ‰' : 'Game Over! Better luck next time!',
        severity: socket.id === winnerId ? 'success' : 'info'
      });
    });

    socket.on('playerLeft', () => {
      setNotification({
        open: true,
        message: 'Your opponent has left the game',
        severity: 'warning'
      });
      onExit();
    });

    return () => {
      socket.off('gameStart');
      socket.off('submissionResult');
      socket.off('gameOver');
      socket.off('playerLeft');
    };
  }, [roomId, onExit, language]);

  useEffect(() => {
    let timer;
    if (status === 'started' && timeLeft > 0) {
      timer = setInterval(() => {
        const elapsed = Math.floor((Date.now() - startTime) / 1000);
        const remaining = Math.max(0, challenge.timeLimit - elapsed);
        
        setTimeLeft(remaining);
        
        if (remaining === 0) {
          setStatus('finished');
          setNotification({
            open: true,
            message: 'Time is up!',
            severity: 'warning'
          });
        }
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [status, startTime, challenge, timeLeft]);

  const handleLanguageChange = (event) => {
    const newLang = event.target.value;
    setLanguage(newLang);
    if (challenge && challenge.boilerplateCode[newLang]) {
      setCode(challenge.boilerplateCode[newLang]);
    }
  };

  const handleSubmit = () => {
    if (!code.trim()) {
      setNotification({
        open: true,
        message: 'Please write some code before submitting',
        severity: 'warning'
      });
      return;
    }

    setNotification({
      open: true,
      message: 'Testing your solution...',
      severity: 'info'
    });

    socket.emit('submitSolution', {
      roomId,
      code,
      language
    });
  };

  const handleCloseNotification = () => {
    setNotification(prev => ({ ...prev, open: false }));
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  if (!challenge) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white'
      }}>
        <div style={{ fontSize: '18px' }}>Loading challenge...</div>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, sans-serif'
    }}>
      <div style={{
        maxWidth: '1400px',
        margin: '0 auto',
        display: 'grid',
        gridTemplateColumns: '1fr 2fr',
        gap: '30px',
        height: 'calc(100vh - 40px)'
      }}>
        {/* Challenge Description Panel */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)',
          borderRadius: '20px',
          padding: '30px',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          overflow: 'auto',
          color: 'white'
        }}>
          <div style={{ marginBottom: '25px' }}>
            <h1 style={{
              fontSize: '28px',
              fontWeight: '700',
              margin: '0 0 15px 0',
              color: 'white'
            }}>
              {challenge.title}
            </h1>
            <div style={{
              display: 'inline-block',
              padding: '6px 16px',
              borderRadius: '20px',
              fontSize: '12px',
              fontWeight: '600',
              textTransform: 'uppercase',
              letterSpacing: '1px',
              background: challenge.difficulty === 'easy' ? 'rgba(76, 175, 80, 0.2)' :
                         challenge.difficulty === 'medium' ? 'rgba(255, 152, 0, 0.2)' :
                         'rgba(244, 67, 54, 0.2)',
              color: challenge.difficulty === 'easy' ? '#4caf50' :
                     challenge.difficulty === 'medium' ? '#ff9800' : '#f44336',
              border: `1px solid ${challenge.difficulty === 'easy' ? '#4caf50' :
                       challenge.difficulty === 'medium' ? '#ff9800' : '#f44336'}`
            }}>
              {challenge.difficulty}
            </div>
          </div>

          <div style={{ marginBottom: '25px' }}>
            <p style={{
              fontSize: '16px',
              lineHeight: '1.6',
              margin: '0',
              whiteSpace: 'pre-line'
            }}>
              {challenge.description}
            </p>
          </div>

          <div style={{ marginBottom: '25px' }}>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '600',
              margin: '0 0 10px 0',
              color: 'white'
            }}>
              Input Format
            </h3>
            <p style={{
              fontSize: '14px',
              lineHeight: '1.5',
              margin: '0',
              fontFamily: 'monospace',
              background: 'rgba(0, 0, 0, 0.2)',
              padding: '10px',
              borderRadius: '8px'
            }}>
              {challenge.inputFormat}
            </p>
          </div>

          <div style={{ marginBottom: '25px' }}>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '600',
              margin: '0 0 10px 0',
              color: 'white'
            }}>
              Output Format
            </h3>
            <p style={{
              fontSize: '14px',
              lineHeight: '1.5',
              margin: '0',
              fontFamily: 'monospace',
              background: 'rgba(0, 0, 0, 0.2)',
              padding: '10px',
              borderRadius: '8px'
            }}>
              {challenge.outputFormat}
            </p>
          </div>

          <div style={{ marginBottom: '25px' }}>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '600',
              margin: '0 0 10px 0',
              color: 'white'
            }}>
              Constraints
            </h3>
            <ul style={{
              margin: '0',
              paddingLeft: '20px',
              fontSize: '14px',
              lineHeight: '1.5'
            }}>
              {challenge.constraints.map((constraint, index) => (
                <li key={index} style={{ marginBottom: '5px' }}>{constraint}</li>
              ))}
            </ul>
          </div>

          <div>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '600',
              margin: '0 0 15px 0',
              color: 'white'
            }}>
              Examples
            </h3>
            {challenge.examples.map((example, index) => (
              <div key={index} style={{
                background: 'rgba(0, 0, 0, 0.2)',
                borderRadius: '12px',
                padding: '20px',
                marginBottom: '15px',
                border: '1px solid rgba(255, 255, 255, 0.1)'
              }}>
                <div style={{ marginBottom: '15px' }}>
                  <div style={{
                    fontSize: '12px',
                    fontWeight: '600',
                    color: 'rgba(255, 255, 255, 0.7)',
                    marginBottom: '5px'
                  }}>
                    Input:
                  </div>
                  <div style={{
                    fontSize: '14px',
                    fontFamily: 'monospace',
                    background: 'rgba(0, 0, 0, 0.3)',
                    padding: '8px',
                    borderRadius: '6px',
                    color: '#4caf50'
                  }}>
                    {example.input}
                  </div>
                </div>
                <div style={{ marginBottom: '15px' }}>
                  <div style={{
                    fontSize: '12px',
                    fontWeight: '600',
                    color: 'rgba(255, 255, 255, 0.7)',
                    marginBottom: '5px'
                  }}>
                    Output:
                  </div>
                  <div style={{
                    fontSize: '14px',
                    fontFamily: 'monospace',
                    background: 'rgba(0, 0, 0, 0.3)',
                    padding: '8px',
                    borderRadius: '6px',
                    color: '#2196f3'
                  }}>
                    {example.output}
                  </div>
                </div>
                <div>
                  <div style={{
                    fontSize: '12px',
                    fontWeight: '600',
                    color: 'rgba(255, 255, 255, 0.7)',
                    marginBottom: '5px'
                  }}>
                    Explanation:
                  </div>
                  <div style={{
                    fontSize: '14px',
                    lineHeight: '1.5',
                    whiteSpace: 'pre-line'
                  }}>
                    {example.explanation}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Code Editor Panel */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)',
          borderRadius: '20px',
          padding: '30px',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          display: 'flex',
          flexDirection: 'column'
        }}>
          {/* Header Controls */}
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '25px'
          }}>
            <select
              value={language}
              onChange={handleLanguageChange}
              style={{
                background: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.3)',
                borderRadius: '10px',
                padding: '10px 15px',
                color: 'white',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                outline: 'none'
              }}
            >
              <option value="python">Python</option>
              <option value="cpp">C++</option>
              <option value="java">Java</option>
            </select>

            {timeLeft !== null && (
              <div style={{
                fontSize: '20px',
                fontWeight: '700',
                color: timeLeft < 60 ? '#ff6b6b' : 'white',
                padding: '10px 20px',
                background: 'rgba(0, 0, 0, 0.2)',
                borderRadius: '10px',
                border: `1px solid ${timeLeft < 60 ? '#ff6b6b' : 'rgba(255, 255, 255, 0.3)'}`
              }}>
                {formatTime(timeLeft)}
              </div>
            )}
          </div>

          {/* Code Editor */}
          <div style={{ flex: 1, marginBottom: '25px' }}>
            <EditorWrapper
              value={code}
              onChange={(value) => setCode(value || '')}
              language={language}
              theme="vs-dark"
              options={{
                minimap: { enabled: false },
                fontSize: 16,
                lineNumbers: 'on',
                automaticLayout: true,
                scrollBeyondLastLine: false,
                tabSize: 4,
                wordWrap: 'on',
                overviewRulerBorder: false,
                scrollbar: {
                  vertical: 'auto',
                  horizontal: 'auto',
                  verticalScrollbarSize: 10,
                  horizontalScrollbarSize: 10
                }
              }}
            />
          </div>

          {/* Action Buttons */}
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <button
              onClick={handleSubmit}
              disabled={status !== 'started' || timeLeft === 0}
              style={{
                background: status !== 'started' || timeLeft === 0 ? 
                  'rgba(255, 255, 255, 0.1)' : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                border: '1px solid rgba(255, 255, 255, 0.3)',
                borderRadius: '12px',
                padding: '15px 30px',
                color: 'white',
                fontSize: '16px',
                fontWeight: '600',
                cursor: status !== 'started' || timeLeft === 0 ? 'not-allowed' : 'pointer',
                transition: 'all 0.3s ease',
                minWidth: '180px'
              }}
            >
              {status === 'waiting' ? 'Waiting for players...' : 
               timeLeft === 0 ? 'Time is up!' : 
               'Submit Solution'}
            </button>

            <button
              onClick={onExit}
              style={{
                background: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.3)',
                borderRadius: '12px',
                padding: '15px 30px',
                color: 'white',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                minWidth: '150px'
              }}
            >
              Exit Challenge
            </button>
          </div>

          {/* Test Results */}
          {result && result.results && (
            <div style={{
              marginTop: '25px',
              background: 'rgba(0, 0, 0, 0.2)',
              borderRadius: '15px',
              padding: '25px',
              border: '1px solid rgba(255, 255, 255, 0.1)'
            }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                marginBottom: '20px'
              }}>
                <h3 style={{
                  fontSize: '20px',
                  fontWeight: '600',
                  margin: '0 15px 0 0',
                  color: 'white'
                }}>
                  Test Results
                </h3>
                <div style={{
                  padding: '6px 16px',
                  borderRadius: '20px',
                  fontSize: '12px',
                  fontWeight: '600',
                  background: result.passed ? 'rgba(76, 175, 80, 0.2)' : 'rgba(244, 67, 54, 0.2)',
                  color: result.passed ? '#4caf50' : '#f44336',
                  border: `1px solid ${result.passed ? '#4caf50' : '#f44336'}`
                }}>
                  {result.passed ? 'All Tests Passed' : 'Some Tests Failed'}
                </div>
              </div>

              {result.results.map((testResult, index) => (
                <div key={index} style={{
                  background: 'rgba(255, 255, 255, 0.05)',
                  borderRadius: '12px',
                  padding: '20px',
                  marginBottom: '15px',
                  border: `1px solid ${testResult.passed ? 'rgba(76, 175, 80, 0.3)' : 'rgba(244, 67, 54, 0.3)'}`
                }}>
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginBottom: '15px'
                  }}>
                    <h4 style={{
                      fontSize: '16px',
                      fontWeight: '600',
                      margin: '0',
                      color: testResult.passed ? '#4caf50' : '#f44336'
                    }}>
                      Test Case {index + 1}
                    </h4>
                    <div style={{
                      padding: '4px 12px',
                      borderRadius: '15px',
                      fontSize: '11px',
                      fontWeight: '600',
                      background: testResult.passed ? 'rgba(76, 175, 80, 0.2)' : 'rgba(244, 67, 54, 0.2)',
                      color: testResult.passed ? '#4caf50' : '#f44336',
                      border: `1px solid ${testResult.passed ? '#4caf50' : '#f44336'}`
                    }}>
                      {testResult.passed ? 'Passed' : 'Failed'}
                    </div>
                  </div>

                  <div style={{ paddingLeft: '15px' }}>
                    <div style={{ marginBottom: '10px' }}>
                      <div style={{
                        fontSize: '12px',
                        fontWeight: '600',
                        color: 'rgba(255, 255, 255, 0.7)',
                        marginBottom: '5px'
                      }}>
                        Input:
                      </div>
                      <div style={{
                        fontSize: '13px',
                        fontFamily: 'monospace',
                        background: 'rgba(0, 0, 0, 0.3)',
                        padding: '8px',
                        borderRadius: '6px',
                        color: '#4caf50'
                      }}>
                        {testResult.input}
                      </div>
                    </div>

                    <div style={{ marginBottom: '10px' }}>
                      <div style={{
                        fontSize: '12px',
                        fontWeight: '600',
                        color: 'rgba(255, 255, 255, 0.7)',
                        marginBottom: '5px'
                      }}>
                        Expected Output:
                      </div>
                      <div style={{
                        fontSize: '13px',
                        fontFamily: 'monospace',
                        background: 'rgba(0, 0, 0, 0.3)',
                        padding: '8px',
                        borderRadius: '6px',
                        color: '#2196f3'
                      }}>
                        {testResult.expected}
                      </div>
                    </div>

                    <div style={{ marginBottom: '10px' }}>
                      <div style={{
                        fontSize: '12px',
                        fontWeight: '600',
                        color: 'rgba(255, 255, 255, 0.7)',
                        marginBottom: '5px'
                      }}>
                        Your Output:
                      </div>
                      <div style={{
                        fontSize: '13px',
                        fontFamily: 'monospace',
                        background: 'rgba(0, 0, 0, 0.3)',
                        padding: '8px',
                        borderRadius: '6px',
                        color: testResult.passed ? '#4caf50' : '#f44336'
                      }}>
                        {testResult.actual}
                      </div>
                    </div>

                    {testResult.error && (
                      <div style={{ marginBottom: '10px' }}>
                        <div style={{
                          fontSize: '12px',
                          fontWeight: '600',
                          color: '#f44336',
                          marginBottom: '5px'
                        }}>
                          Error:
                        </div>
                        <div style={{
                          fontSize: '13px',
                          color: '#f44336',
                          background: 'rgba(244, 67, 54, 0.1)',
                          padding: '8px',
                          borderRadius: '6px',
                          border: '1px solid rgba(244, 67, 54, 0.3)'
                        }}>
                          {testResult.error}
                        </div>
                      </div>
                    )}

                    {testResult.time && (
                      <div style={{
                        fontSize: '12px',
                        color: 'rgba(255, 255, 255, 0.6)'
                      }}>
                        Execution time: {testResult.time}ms
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Notification */}
      {notification.open && (
        <div style={{
          position: 'fixed',
          bottom: '30px',
          right: '30px',
          background: notification.severity === 'error' ? 'rgba(244, 67, 54, 0.9)' :
                     notification.severity === 'success' ? 'rgba(76, 175, 80, 0.9)' :
                     notification.severity === 'warning' ? 'rgba(255, 152, 0, 0.9)' :
                     'rgba(33, 150, 243, 0.9)',
          color: 'white',
          padding: '15px 25px',
          borderRadius: '12px',
          fontSize: '14px',
          fontWeight: '500',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          zIndex: 1000,
          cursor: 'pointer'
        }} onClick={handleCloseNotification}>
          {notification.message}
        </div>
      )}
    </div>
  );
};

export default ChallengeRoom;
