import React, { useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  Paper,
  Container,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  IconButton,
  Grid,
  Snackbar,
  Alert
} from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import { socket } from '../socket';
import { Link } from 'react-router-dom';

const bgGradient = {
  minHeight: '100vh',
  background: 'linear-gradient(135deg, #0f2027 0%, #2c5364 100%)',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  color: '#fff',
  position: 'relative',
  overflow: 'hidden',
};

const titleStyle = {
  fontSize: '4rem',
  fontWeight: 'bold',
  letterSpacing: '0.1em',
  textShadow: '0 4px 32px #000',
  marginBottom: '1rem',
  fontFamily: 'Montserrat, sans-serif',
};

const subtitleStyle = {
  fontSize: '1.5rem',
  fontWeight: '300',
  marginBottom: '2.5rem',
  fontFamily: 'Montserrat, sans-serif',
  textShadow: '0 2px 8px #000',
};

const buttonStyle = {
  padding: '1rem 2.5rem',
  fontSize: '1.25rem',
  borderRadius: '30px',
  border: 'none',
  background: 'linear-gradient(90deg, #ff512f 0%, #dd2476 100%)',
  color: '#fff',
  fontWeight: 'bold',
  boxShadow: '0 2px 16px #0008',
  cursor: 'pointer',
  textDecoration: 'none',
  transition: 'transform 0.2s',
};

const marqueeStyle = {
  position: 'absolute',
  bottom: 0,
  width: '100%',
  background: 'rgba(0,0,0,0.7)',
  color: '#fff',
  fontSize: '1.1rem',
  padding: '0.5rem 0',
  fontFamily: 'Montserrat, sans-serif',
  letterSpacing: '0.05em',
};

const Home = ({ onStartPractice, onStartChallenge }) => {
  const [roomId, setRoomId] = useState('');
  const [joinDialogOpen, setJoinDialogOpen] = useState(false);
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [error, setError] = useState(null);

  const handleCreateChallenge = () => {
    console.log('DEBUG: Creating new challenge...');
    if (!socket.connected) {
      alert('Socket is not connected!');
      console.error('Socket is not connected!');
      return;
    }
    socket.emit('createChallenge', (response) => {
      console.log('DEBUG: Create challenge response:', response);
      if (response.error) {
        setError(response.error);
        alert('Error from server: ' + response.error);
        return;
      }
      if (response.roomId) {
        setRoomId(response.roomId);
        setCreateDialogOpen(true);
        alert('Challenge created! Room ID: ' + response.roomId);
      } else {
        alert('No roomId received from server!');
      }
    });
  };

  const handleStartChallenge = () => {
    setCreateDialogOpen(false);
    onStartChallenge(roomId);
  };

  const handleJoinChallenge = () => {
    if (roomId.trim()) {
      onStartChallenge(roomId);
      setJoinDialogOpen(false);
    }
  };

  const handleCopyRoomId = () => {
    navigator.clipboard.writeText(roomId);
  };

  const handleCloseError = () => {
    setError(null);
  };

  return (
    <div style={bgGradient}>
      <div style={titleStyle}>CodeArena</div>
      <div style={subtitleStyle}>
        Unleash your coding skills. Compete, learn, and grow in the ultimate online code challenge platform.
      </div>
      <Link to="/login" style={buttonStyle}>
        Enter the Arena (Login)
      </Link>
      <div style={marqueeStyle}>
        <marquee behavior="scroll" direction="left" scrollamount="7">
          Welcome to CodeArena! &nbsp;|&nbsp; Real-time code execution for Java, Python, C++, and more! &nbsp;|&nbsp; Secure, fast, and fun. &nbsp;|&nbsp; Compete with friends, track your progress, and become a coding champion! &nbsp;|&nbsp; Powered by Docker and MongoDB. &nbsp;|&nbsp; Register now and join the battle!
        </marquee>
      </div>
    </div>
  );
};

export default Home;
