import React, { useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogActions from '@mui/material/DialogActions';
import IconButton from '@mui/material/IconButton';
import Grid from '@mui/material/Grid';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import Chip from '@mui/material/Chip';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import AutoAwesomeIcon from '@mui/icons-material/AutoAwesome';
import CodeIcon from '@mui/icons-material/Code';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import LogoutIcon from '@mui/icons-material/Logout';
import CodingChallenge from './CodingChallenge';
import ChallengeRoom from './ChallengeRoom';
import PracticeRoom from './PracticeRoom';
import { socket } from '../socket';
import './Login.css';
import Select from '@mui/material/Select';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';

const runningTexts = [
  'Real-time coding battles',
  'Practice and improve your skills',
  'Challenge your friends',
  'Supports C++, Java, Python',
  'Instant code execution',
  'Fun, competitive, and educational!'
];

function Home({ user, onLogout }) {
  const [mode, setMode] = useState('home'); // 'home', 'practice', 'challenge'
  const [roomId, setRoomId] = useState('');
  const [challenge, setChallenge] = useState(null);
  const [joinDialogOpen, setJoinDialogOpen] = useState(false);
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [joinRoomId, setJoinRoomId] = useState('');
  const [error, setError] = useState('');
  const [snackbar, setSnackbar] = useState('');
  const [loading, setLoading] = useState(false);
  const [textIndex, setTextIndex] = useState(0);
  const [profileAnchorEl, setProfileAnchorEl] = useState(null);
  const [challengeSource, setChallengeSource] = useState(null);
  const [selectedMode, setSelectedMode] = useState('');
  const [selectedDifficulty, setSelectedDifficulty] = useState('');

  React.useEffect(() => {
    const interval = setInterval(() => {
      setTextIndex((prev) => (prev + 1) % runningTexts.length);
    }, 2200);
    return () => clearInterval(interval);
  }, []);

  // Profile menu handlers
  const handleProfileClick = (event) => {
    setProfileAnchorEl(event.currentTarget);
  };

  const handleProfileClose = () => {
    setProfileAnchorEl(null);
  };

  const handleLogout = () => {
    handleProfileClose();
    onLogout();
  };

  // Handlers for UI
  const handleStartPractice = () => setMode('practice');

  const handleCreateChallenge = () => {
    setLoading(true);
    socket.emit('createChallenge', (response) => {
      setLoading(false);
      if (response.error) {
        setError(response.error);
        return;
      }
      setRoomId(response.roomId);
      setChallenge(response.challenge);
      setChallengeSource(response.source);
      setCreateDialogOpen(true);
    });
  };

  const handleStartChallenge = () => {
    setCreateDialogOpen(false);
    setMode('challenge');
  };

  const handleJoinChallenge = () => {
    if (!joinRoomId.trim()) {
      setError('Please enter a challenge code.');
      return;
    }
    setLoading(true);
    socket.emit('joinChallenge', joinRoomId.trim(), (response) => {
      setLoading(false);
      if (response.error) {
        setError(response.error);
        return;
      }
      setRoomId(joinRoomId.trim());
      setChallenge(response.challenge);
      setJoinDialogOpen(false);
      setMode('challenge');
    });
  };

  const handleCopyRoomId = () => {
    navigator.clipboard.writeText(roomId);
    setSnackbar('Room ID copied!');
  };

  const handleCloseError = () => setError('');
  const handleCloseSnackbar = () => setSnackbar('');
  const handleExitChallenge = () => {
    setMode('home');
    setRoomId('');
    setJoinRoomId('');
    setChallenge(null);
    setChallengeSource(null);
  };

  // Main UI
  if (mode === 'practice') {
    return <PracticeRoom onExit={handleExitChallenge} user={user} onLogout={onLogout} difficulty={selectedDifficulty} />;
  }
  if (mode === 'challenge') {
    return <ChallengeRoom roomId={roomId} challenge={challenge} challengeSource={challengeSource} onExit={handleExitChallenge} user={user} onLogout={onLogout} difficulty={selectedDifficulty} />;
  }

  // Home screen
  return (
    <div className="auth-container">
      {/* Background with glow orbs */}
      <div className="auth-background">
        <div className="glow-orb glow-orb-1"></div>
        <div className="glow-orb glow-orb-2"></div>
        <div className="glow-orb glow-orb-3"></div>
      </div>

      {/* Status Bar */}
      <Box
        sx={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          height: '70px',
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(20px)',
          borderBottom: '1px solid rgba(255, 255, 255, 0.2)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          padding: '0 30px',
          zIndex: 1000,
        }}
      >
        {/* Logo */}
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <img 
            src="/logo.png" 
            alt="Code Arena" 
            style={{
              height: '40px',
              width: 'auto',
              filter: 'drop-shadow(0 0 10px rgba(102, 126, 234, 0.6))',
            }}
          />
        </Box>

        {/* Profile Section */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Typography
            sx={{
              color: 'rgba(255, 255, 255, 0.9)',
              fontWeight: 500,
              fontSize: '16px',
            }}
          >
            Welcome, {user?.username}!
          </Typography>
          <IconButton
            onClick={handleProfileClick}
            sx={{
              color: 'rgba(255, 255, 255, 0.9)',
              background: 'rgba(255, 255, 255, 0.1)',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              '&:hover': {
                background: 'rgba(255, 255, 255, 0.2)',
                transform: 'scale(1.05)',
              },
              transition: 'all 0.2s ease',
            }}
          >
            <AccountCircleIcon sx={{ fontSize: 28 }} />
          </IconButton>
        </Box>

        {/* Profile Menu */}
        <Menu
          anchorEl={profileAnchorEl}
          open={Boolean(profileAnchorEl)}
          onClose={handleProfileClose}
          PaperProps={{
            sx: {
              background: 'rgba(255, 255, 255, 0.95)',
              backdropFilter: 'blur(20px)',
              border: '1px solid rgba(255, 255, 255, 0.3)',
              borderRadius: 2,
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
              mt: 1,
            }
          }}
        >
          <MenuItem onClick={handleLogout} sx={{ minWidth: 150 }}>
            <ListItemIcon>
              <LogoutIcon fontSize="small" />
            </ListItemIcon>
            <Typography>Logout</Typography>
          </MenuItem>
        </Menu>
      </Box>

      {/* Main Content */}
      <Box
        sx={{
          flex: 1,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          paddingTop: '70px',
          paddingBottom: '40px',
          position: 'relative',
          zIndex: 2,
        }}
      >
        <Container maxWidth="sm">
          {/* Mode & Difficulty Selector Card */}
          <Paper
            elevation={3}
            sx={{
              p: 4,
              mb: 4,
              borderRadius: 4,
              background: 'rgba(255,255,255,0.13)',
              border: '1px solid rgba(255,255,255,0.25)',
              boxShadow: '0 8px 32px rgba(0,0,0,0.10)',
              textAlign: 'center',
            }}
          >
            <Typography variant="h5" sx={{ fontWeight: 700, mb: 2, color: 'rgba(255,255,255,0.95)' }}>
              Select Mode & Difficulty
            </Typography>
            <Typography variant="body1" sx={{ mb: 3, color: 'rgba(255,255,255,0.8)' }}>
              Choose how you want to play and your preferred challenge level.
            </Typography>
            <Grid container spacing={2} justifyContent="center">
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth variant="outlined">
                  <InputLabel id="mode-label">Mode</InputLabel>
                  <Select
                    labelId="mode-label"
                    value={selectedMode}
                    onChange={e => setSelectedMode(e.target.value)}
                    label="Mode"
                    sx={{ background: 'rgba(255,255,255,0.9)', borderRadius: 2 }}
                  >
                    <MenuItem value="practice">Practice</MenuItem>
                    <MenuItem value="challenge">Challenge</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth variant="outlined">
                  <InputLabel id="difficulty-label">Difficulty</InputLabel>
                  <Select
                    labelId="difficulty-label"
                    value={selectedDifficulty}
                    onChange={e => setSelectedDifficulty(e.target.value)}
                    label="Difficulty"
                    sx={{ background: 'rgba(255,255,255,0.9)', borderRadius: 2 }}
                  >
                    <MenuItem value="beginner">Beginner</MenuItem>
                    <MenuItem value="intermediate">Intermediate</MenuItem>
                    <MenuItem value="advanced">Advanced</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
            </Grid>
            <Button
              variant="contained"
              size="large"
              fullWidth
              sx={{
                mt: 4,
                py: 2,
                fontSize: 18,
                fontWeight: 600,
                borderRadius: 3,
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                color: 'white',
                boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
                transition: 'all 0.3s ease',
                '&:hover': {
                  background: 'linear-gradient(135deg, #764ba2 0%, #667eea 100%)',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 6px 20px rgba(102, 126, 234, 0.6)',
                },
                '&:disabled': {
                  background: 'rgba(255, 255, 255, 0.2)',
                  color: 'rgba(255, 255, 255, 0.5)',
                },
              }}
              disabled={!selectedMode || !selectedDifficulty || loading}
              onClick={() => {
                if (selectedMode === 'practice') handleStartPractice();
                else if (selectedMode === 'challenge') handleCreateChallenge();
              }}
            >
              {selectedMode === 'challenge' ? (loading ? 'Creating...' : 'Start Challenge') : 'Start Practice'}
            </Button>
          </Paper>
          {/* Existing Welcome Card (now just info) */}
          <Paper
            elevation={0}
            sx={{
              p: 5,
              borderRadius: 4,
              background: 'rgba(255, 255, 255, 0.1)',
              backdropFilter: 'blur(20px)',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
              mb: 2,
            }}
          >
            <Typography
              variant="h3"
              align="center"
              sx={{
                fontWeight: 700,
                color: 'rgba(255, 255, 255, 0.95)',
                mb: 1,
                fontFamily: 'Montserrat, sans-serif',
                letterSpacing: 1,
              }}
            >
              Welcome to Code Arena
            </Typography>
            <Typography
              variant="h6"
              align="center"
              sx={{
                mb: 4,
                minHeight: 40,
                fontWeight: 400,
                color: 'rgba(255, 255, 255, 0.8)',
                fontFamily: 'Inter, sans-serif',
                transition: 'opacity 0.5s',
                opacity: 1,
              }}
            >
              {runningTexts[textIndex]}
            </Typography>
          </Paper>
        </Container>
      </Box>

      {/* Join Challenge Dialog */}
      <Dialog 
        open={joinDialogOpen} 
        onClose={() => setJoinDialogOpen(false)} 
        PaperProps={{
          sx: {
            borderRadius: 3,
            background: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255, 255, 255, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
          }
        }}
      >
        <DialogTitle sx={{ 
          fontWeight: 700, 
          color: '#333',
          textAlign: 'center',
        }}>
          Join Challenge
        </DialogTitle>
        <DialogContent>
          <DialogContentText sx={{ color: '#666', mb: 2 }}>
            Enter the challenge code provided by your friend:
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            label="Challenge Code"
            fullWidth
            variant="outlined"
            value={joinRoomId}
            onChange={e => setJoinRoomId(e.target.value)}
            sx={{
              '& .MuiOutlinedInput-root': {
                borderRadius: 2,
              },
            }}
          />
        </DialogContent>
        <DialogActions sx={{ p: 3, pt: 1 }}>
          <Button 
            onClick={() => setJoinDialogOpen(false)} 
            sx={{ color: '#666' }}
          >
            Cancel
          </Button>
          <Button 
            onClick={handleJoinChallenge} 
            variant="contained" 
            disabled={loading}
            sx={{
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              color: 'white',
              fontWeight: 600,
              '&:hover': {
                background: 'linear-gradient(135deg, #764ba2 0%, #667eea 100%)',
              },
            }}
          >
            {loading ? 'Joining...' : 'Join'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Create Challenge Dialog */}
      <Dialog 
        open={createDialogOpen} 
        onClose={() => setCreateDialogOpen(false)} 
        PaperProps={{
          sx: {
            borderRadius: 3,
            background: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255, 255, 255, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
          }
        }}
      >
        <DialogTitle sx={{ 
          fontWeight: 700, 
          color: '#333',
          textAlign: 'center',
        }}>
          Challenge Created!
        </DialogTitle>
        <DialogContent>
          <DialogContentText sx={{ color: '#666', mb: 2 }}>
            Share this code with your opponent to start the challenge:
          </DialogContentText>
          {challengeSource && (
            <Box sx={{ mb: 2, display: 'flex', justifyContent: 'center' }}>
              <Chip 
                label={`Generated by ${challengeSource === 'gemini' ? 'AI' : 'Sample Set'}`}
                color={challengeSource === 'gemini' ? 'primary' : 'default'}
                icon={challengeSource === 'gemini' ? <AutoAwesomeIcon /> : <CodeIcon />}
                sx={{ 
                  fontWeight: 600,
                  background: challengeSource === 'gemini' 
                    ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' 
                    : 'rgba(0, 0, 0, 0.1)',
                  color: challengeSource === 'gemini' ? 'white' : '#666',
                }}
              />
            </Box>
          )}
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 2, 
            p: 2,
            background: 'rgba(102, 126, 234, 0.1)',
            borderRadius: 2,
            border: '1px solid rgba(102, 126, 234, 0.2)',
          }}>
            <Typography 
              variant="h6" 
              component="div" 
              sx={{ 
                flexGrow: 1, 
                color: '#333', 
                letterSpacing: 2, 
                fontWeight: 700,
                fontFamily: 'monospace',
              }}
            >
              {roomId}
            </Typography>
            <IconButton 
              onClick={handleCopyRoomId} 
              color="primary" 
              size="small"
              sx={{ 
                color: '#667eea',
                '&:hover': {
                  background: 'rgba(102, 126, 234, 0.1)',
                },
              }}
            >
              <ContentCopyIcon />
            </IconButton>
          </Box>
        </DialogContent>
        <DialogActions sx={{ p: 3, pt: 1 }}>
          <Button 
            onClick={() => setCreateDialogOpen(false)} 
            sx={{ color: '#666' }}
          >
            Cancel
          </Button>
          <Button 
            onClick={handleStartChallenge} 
            variant="contained"
            sx={{
              background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
              color: 'white',
              fontWeight: 600,
              '&:hover': {
                background: 'linear-gradient(135deg, #f5576c 0%, #f093fb 100%)',
              },
            }}
          >
            Start Challenge
          </Button>
        </DialogActions>
      </Dialog>

      {/* Error Snackbar */}
      <Snackbar
        open={!!error}
        autoHideDuration={3000}
        onClose={handleCloseError}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseError} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>

      {/* Success Snackbar */}
      <Snackbar
        open={!!snackbar}
        autoHideDuration={2000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
          {snackbar}
        </Alert>
      </Snackbar>
    </div>
  );
}

export default Home;
