import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const containerStyle = {
  maxWidth: 400,
  margin: '60px auto',
  padding: 32,
  background: 'rgba(44,83,100,0.95)',
  borderRadius: 16,
  boxShadow: '0 8px 32px #0008',
  color: '#fff',
  fontFamily: 'Montserrat, sans-serif',
};

const inputStyle = {
  width: '100%',
  padding: '0.75rem',
  marginBottom: 16,
  borderRadius: 8,
  border: 'none',
  fontSize: '1rem',
  background: '#f0f0f0',
  color: '#222',
};

const buttonStyle = {
  width: '100%',
  padding: '0.75rem',
  borderRadius: 8,
  border: 'none',
  fontSize: '1.1rem',
  fontWeight: 'bold',
  background: 'linear-gradient(90deg, #ff512f 0%, #dd2476 100%)',
  color: '#fff',
  cursor: 'pointer',
  marginBottom: 12,
};

const switchStyle = {
  background: 'none',
  border: 'none',
  color: '#5b86e5',
  cursor: 'pointer',
  textDecoration: 'underline',
  fontSize: '1rem',
  marginTop: 8,
};

function LoginPage() {
  const [isRegister, setIsRegister] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    const endpoint = isRegister ? '/api/register' : '/api/login';
    try {
      const res = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });
      const data = await res.json();
      if (res.ok) {
        setMessage(isRegister ? 'Registration successful! Please log in.' : 'Login successful! Redirecting...');
        if (!isRegister && data.token) {
          localStorage.setItem('token', data.token);
          setTimeout(() => navigate('/arena'), 1000);
        } else if (isRegister) {
          setIsRegister(false);
        }
      } else {
        setMessage(data.error || 'Error');
      }
    } catch (err) {
      setMessage('Network error');
    }
    setLoading(false);
  };

  return (
    <div style={containerStyle}>
      <h2 style={{ textAlign: 'center', marginBottom: 24 }}>{isRegister ? 'Register' : 'Login'} to CodeArena</h2>
      <form onSubmit={handleSubmit}>
        <input
          style={inputStyle}
          type="text"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
          required
        />
        <input
          style={inputStyle}
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        <button type="submit" style={buttonStyle} disabled={loading}>
          {loading ? (isRegister ? 'Registering...' : 'Logging in...') : (isRegister ? 'Register' : 'Login')}
        </button>
      </form>
      <button style={switchStyle} onClick={() => { setIsRegister(!isRegister); setMessage(''); }}>
        {isRegister ? 'Already have an account? Login' : "Don't have an account? Register"}
      </button>
      {message && <div style={{ marginTop: 16, color: message.includes('success') ? '#36d1c4' : '#ff512f', textAlign: 'center' }}>{message}</div>}
    </div>
  );
}

export default LoginPage;
