import React from 'react';
import CodingChallenge from './components/CodingChallenge';
import ChallengeRoom from './components/ChallengeRoom';
import OldHome from './components/OldHome';

class App extends React.Component {
  state = {
    mode: 'home', // 'home', 'practice', 'challenge'
    roomId: '',
  };

  handleStartPractice = () => this.setState({ mode: 'practice' });
  handleStartChallenge = (roomId) => this.setState({ mode: 'challenge', roomId });
  handleExitChallenge = () => this.setState({ mode: 'home', roomId: '' });

  render() {
    const { mode, roomId } = this.state;
    return (
      <>
        {mode === 'home' && (
          <OldHome
            onStartPractice={this.handleStartPractice}
            onStartChallenge={this.handleStartChallenge}
          />
        )}
        {mode === 'practice' && <CodingChallenge />}
        {mode === 'challenge' && (
          <ChallengeRoom
            roomId={roomId}
            onExit={this.handleExitChallenge}
          />
        )}
      </>
    );
  }
}

export default App;
