import React, { useState, useEffect } from 'react';
import { Box, Button, Typography, Paper, CircularProgress } from '@mui/material';
import Editor from "@monaco-editor/react";
import { socket } from '../socket';

const ChallengeRoom = ({ roomId, onExit }) => {
  const [challenge, setChallenge] = useState(null);
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [status, setStatus] = useState('waiting');
  const [result, setResult] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);

  useEffect(() => {
    if (!roomId) return;

    socket.emit('joinChallenge', roomId, (response) => {
      if (response.error) {
        alert(response.error);
        onExit();
        return;
      }
      if (!response.challenge) {
        alert('Failed to generate challenge. Please try again.');
        onExit();
        return;
      }
      setChallenge(response.challenge);
    });

    socket.on('gameStart', ({ challenge }) => {
      if (!challenge) {
        alert('Failed to start game. Please try again.');
        onExit();
        return;
      }
      setChallenge(challenge);
      setStatus('started');
      setTimeLeft(challenge.timeLimit || 30); // Default to 30 seconds if timeLimit is not set
    });

    socket.on('submissionResult', (validation) => {
      setResult(validation);
    });

    socket.on('gameOver', ({ winnerId, solutions }) => {
      setStatus('finished');
      setResult({
        isWinner: socket.id === winnerId,
        solutions
      });
    });

    socket.on('playerLeft', () => {
      alert('Your opponent has left the game');
      onExit();
    });

    return () => {
      socket.off('gameStart');
      socket.off('submissionResult');
      socket.off('gameOver');
      socket.off('playerLeft');
    };
  }, [roomId, onExit]);

  useEffect(() => {
    let timer;
    if (status === 'started' && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            clearInterval(timer);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [status, timeLeft]);

  const handleSubmit = () => {
    socket.emit('submitSolution', {
      roomId,
      code,
      language
    });
  };

  if (!challenge) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="100vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box p={3}>
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          {challenge.title}
        </Typography>
        <Typography variant="body1" paragraph>
          {challenge.description}
        </Typography>
        <Typography variant="h6" gutterBottom>
          Examples:
        </Typography>
        {challenge.examples.map((example, index) => (
          <Box key={index} sx={{ mb: 2 }}>
            <Typography variant="body2">
              Input: {example.input}
            </Typography>
            <Typography variant="body2">
              Output: {example.output}
            </Typography>
          </Box>
        ))}
        <Typography variant="h6" color="primary">
          Time Left: {timeLeft} seconds
        </Typography>
      </Paper>

      <Editor
        height="400px"
        language={language}
        value={code}
        onChange={setCode}
        theme="vs-dark"
        options={{
          minimap: { enabled: false },
          fontSize: 14,
        }}
      />

      <Box sx={{ mt: 2, display: 'flex', justifyContent: 'space-between' }}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleSubmit}
          disabled={status !== 'started'}
        >
          Submit Solution
        </Button>
        <Button
          variant="outlined"
          color="secondary"
          onClick={onExit}
        >
          Exit Challenge
        </Button>
      </Box>

      {result && (
        <Paper elevation={3} sx={{ mt: 3, p: 3 }}>
          {result.isWinner !== undefined ? (
            <Typography variant="h5" color={result.isWinner ? "success.main" : "error.main"}>
              {result.isWinner ? "You Won! ðŸŽ‰" : "Better luck next time!"}
            </Typography>
          ) : (
            <>
              <Typography variant="h6" gutterBottom>
                Test Results:
              </Typography>
              {result.results.map((test, index) => (
                <Box key={index} sx={{ mb: 1 }}>
                  <Typography color={test.passed ? "success.main" : "error.main"}>
                    Test {index + 1}: {test.passed ? "Passed âœ“" : "Failed âœ—"}
                  </Typography>
                  {!test.passed && (
                    <>
                      <Typography variant="body2">Input: {test.input}</Typography>
                      <Typography variant="body2">Expected: {test.expected}</Typography>
                      <Typography variant="body2">Got: {test.actual}</Typography>
                    </>
                  )}
                </Box>
              ))}
            </>
          )}
        </Paper>
      )}
    </Box>
  );
};

export default ChallengeRoom;
